import Model from '../UI/Modal';
import classes from './Cart.module.css'

const Cart = props => {
    const cardItems = 
    <ul className={classes['cart-items']}>
        {[{id: 'c1', name:'Sushi', amount:2, price: 12.00}].map((item)=> <li>{item.name}</li>)}</ul>;
    return (<Model>
                {cardItems}
                <div className={classes.total}>
                    <span>Total Amount</span>
                    <span>35.62</span>  
                </div>
                <div className={classes.actions}>
                    <button className={classes['button--alt']} onClick={props.on}>Close</button>
                    <button className={classes.button}>Order</button>
                </div>
            </Model>
            )
}

export default Cart;
